import React, { useState } from 'react';
import Router from 'next/router';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

const SearchResult = ({ searchresult }) => {
	const [spacing, setSpacing] = useState(2);
	let moviesList = [];
	const movies = searchresult.data.Search;

	const handleClick = Id => {
		Router.push(`/[movieId]`, `/${Id}`);
	};

	if (movies) {
		// User has not submitted the form, we are diplaying the array results
		<Grid container spacing={2}>
			<Grid item xs={6}>
				<Grid container justify='center' spacing={spacing}>
					{
						(moviesList = movies.map(movie => {
							return (
								<Grid key={movie.imdbID} className='movie' item>
									<Paper elevation={5}>
										<a onClick={() => handleClick(movie.imdbID)}>
											{' '}
											<img
												src={movie.Poster}
												className='moviePoster'
												alt='Movie poster'
											/>
											<p className='movieTitle'>
												{movie.Title} ({movie.Year})
											</p>
										</a>
									</Paper>
								</Grid>
							);
						}))
					}
					;
				</Grid>
			</Grid>
		</Grid>;
	}

	// if (movies) {
	// User has not submitted the form, we are diplaying the array results
	// moviesList = movies.map(movie => {
	// 	return (
	// 		<Grid container spacing={2}>
	// 			<Grid item xs={12}>
	// 				<Grid container spacing={spacing}>
	// 					<Grid key={movie.imdbID} className='movie' item>
	// 						<Paper elevation={5}>
	// 							<a onClick={() => handleClick(movie.imdbID)}>
	// 								{' '}
	// 								<img
	// 									src={movie.Poster}
	// 									className='moviePoster'
	// 									alt='Movie poster'
	// 								/>
	// 								<p className='movieTitle'>
	// 									{movie.Title} ({movie.Year})
	// 								</p>
	// 							</a>
	// 						</Paper>
	// 					</Grid>
	// 				</Grid>
	// 			</Grid>
	// 		</Grid>
	// 	);
	// });
	// }
	else {
		// User has submiited the form, we are displaying the best matched result
		moviesList.push(searchresult.data);
		moviesList = moviesList.map(movie => {
			return (
				<div key={movie.imdbID} className='movie'>
					<a onClick={() => handleClick(movie.imdbID)}>
						<img src={movie.Poster} className='moviePoster' alt='Movie poster' />
						<p className='movieTitle'>
							{movie.Title} ({movie.Year})
						</p>
					</a>
				</div>
			);
		});
	}
	return (
		<div>
			<p style={{ textAlign: 'center', textShadow: '4px 4px 9px black' }}>
				Search Results...
			</p>
			{moviesList}
		</div>
	);
};

export default SearchResult;
