const WordResults = ({ data }) => {
	const [words, setWords] = useState([]);
	const router = useRouter();
	let wordList = [];

	useEffect(() => {
		const config = {
			'Content-Type': 'application/json',
		};
		Axios.get(`/api/${router.query.movieId}/dict`, config).then(res => {
			console.log(res);
			setWords(res);
		});
