import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SearchResult from './SearchResult';
import TextField from '@material-ui/core/TextField';

const SearchMovies = () => {
	const [searchFormData, setSearchFormData] = useState('');
	const [searchResult, setSearchResult] = useState({});
	const urlforSearch = `http://www.omdbapi.com/?s="${searchFormData}"&type=movie&apikey=${process.env.OmdbKey}`;
	const urlforTitle = `http://www.omdbapi.com/?t="${searchFormData}"&type=movie&apikey=9bc37d14`;

	const handleChange = async e => {
		setSearchFormData(e.target.value);
	};

	const handleSubmit = async e => {
		e.preventDefault();
		const res = await axios.get(urlforTitle);
		if (res.data.Response === 'True') {
			setSearchResult(res);
		} else {
			setSearchResult(res);
		}
	};

	useEffect(() => {
		console.log(`1`);
		axios.get(urlforSearch).then(res => {
			if (res.data.Response === 'True') {
				setSearchResult(res);
			} else {
				setSearchResult(res);
			}
		});
	}, [searchFormData]);

	return (
		<div>
			<form
				noValidate
				autoComplete='off'
				onSubmit={e => handleSubmit(e)}
				style={{ textAlign: 'center', paddingTop: '15px' }}
			>
				<TextField
					id='standard-basic'
					type='text'
					value={searchFormData}
					onChange={e => handleChange(e)}
					label='Search for movies'
				/>
			</form>
			<br />
			{searchFormData ? (
				searchResult.data && searchResult.data.Response === 'True' ? (
					<SearchResult searchresult={searchResult} />
				) : (
					searchResult.data &&
					searchResult.data.Response === 'False' && <p>{searchResult.data.Error}</p>
				)
			) : (
				<p> Nothing to search!!! </p>
			)}
		</div>
	);
};

export default SearchMovies;
