import React from 'react';
import ExposurePlus1TwoToneIcon from '@material-ui/icons/ExposurePlus1TwoTone';
import ExposureNeg1TwoToneIcon from '@material-ui/icons/ExposureNeg1TwoTone';
import Axios from 'axios';

const WordResults = ({ words }) => {
	const handleClick = async (word_id, vote) => {
		const word_id_var = {
			word_id: `${word_id}`,
			vote: `${vote}`,
		};
		const body = JSON.stringify(word_id_var);
		console.log(body);
		const config = {
			headers: {
				'Content-Type': 'application/json',
			},
		};
		const res = await Axios.post(`/api/:id/dict/${word_id}`, body, config);
		// console.log(res);
	};

	return (
		<div>
			{words.data ? (
				words.data.length ? (
					<div>
						{words.data.map(each => {
							return (
								<span key={each.id}>
									{each.word} {'  '}
									<ExposurePlus1TwoToneIcon
										onClick={() => handleClick(each.id, +1)}
									/>{' '}
									{'  '}{' '}
									<ExposureNeg1TwoToneIcon
										onClick={() => handleClick(each.id, -1)}
									/>
									{'  '}
								</span>
							);
						})}
					</div>
				) : (
					<p>Nothing found!!!</p>
				)
			) : (
				<p>Loading...</p>
			)}
		</div>
	);
};

export default WordResults;
