import React, { Fragment, useState } from 'react';
import { useRouter } from 'next/router';
import Axios from 'axios';
import fetch from 'isomorphic-unfetch';

const dictionary = ({ data }) => {
	const [searchWord, setSearchWord] = useState('');
	const router = useRouter();

	const handleChange = async e => {
		setSearchWord(e.target.value);
	};

	const {
		imdbID,
		Title,
		Year,
		Poster,
		Genre,
		Director,
		Actors,
		Metascore,
		imdbRating,
		imdbVotes,
	} = data;

	const handleSubmit = async e => {
		e.preventDefault();
		const searchForm = {
			imdbID,
			Title,
			searchWord,
			Year,
			Poster,
			Genre,
			Director,
			Actors,
			Metascore,
			imdbRating,
			imdbVotes,
		};
		const config = {
			headers: {
				'Content-Type': 'application/json',
			},
		};
		const body = JSON.stringify(searchForm);

		await Axios.post(`/movies/${router.query.movieId}/dict`, body, config);
	};
	const { movieId } = router.query;
	return (
		<div>
			<p>Movie Title: {data.Title}</p>
			{movieId}
			<form onSubmit={e => handleSubmit(e)}>
				<label>Search</label>
				<input type='text' value={searchWord} onChange={e => handleChange(e)}></input>
			</form>
			<p>Hello dictionary!!!</p>
		</div>
	);
};

dictionary.getInitialProps = async ctx => {
	if (ctx.query.movieId) {
		const res = await fetch(
			`http://www.omdbapi.com/?i=${ctx.query.movieId}&type=movie&apikey=${process.env.OmdbKey}`
		);
		const data = await res.json();
		return { data };
	}
};

export default dictionary;
