import React, { Fragment, useState } from 'react';
import Axios from 'axios';
import fetch from 'isomorphic-unfetch';
import { useRouter } from 'next/router';
// import WordResults from '../../components/WordResults';

const dictionary = ({ data }) => {
	const [searchWord, setSearchWord] = useState('');

	const handleChange = async e => {
		setSearchWord(e.target.value);
	};
	const router = useRouter();
	const {
		imdbID,
		Title,
		Year,
		Poster,
		Genre,
		Director,
		Actors,
		Metascore,
		imdbRating,
		imdbVotes,
	} = data;

	const handleSubmit = async e => {
		e.preventDefault();
		const searchForm = {
			imdbID,
			Title,
			searchWord,
			Year,
			Poster,
			Genre,
			Director,
			Actors,
			Metascore,
			imdbRating,
			imdbVotes,
		};
		try {
			const config = {
				headers: {
					'Content-Type': 'application/json',
				},
			};
			const body = JSON.stringify(searchForm);
			const res = await Axios.post(`/api/${router.query.movieId}/dict`, body, config);
			setSearchWord('');
			alert(res.data);
		} catch (err) {
			if (err) throw err;
			console.log(err);
		}
	};

	return (
		<Fragment>
			<p>Movie Title: {data.Title}</p>
			<form onSubmit={e => handleSubmit(e)}>
				<label>Search</label>
				<input type='text' value={searchWord} onChange={e => handleChange(e)}></input>
			</form>
			{/* <WordResults data={data} words={words} /> */}
		</Fragment>
	);
};

dictionary.getInitialProps = async ctx => {
	if (ctx.query.movieId) {
		const res = await fetch(
			`http://www.omdbapi.com/?i=${ctx.query.movieId}&type=movie&apikey=${process.env.OmdbKey}`
		);
		const data = await res.json();
		return { data };
	}
};

export default dictionary;
