import axios from 'axios';
import { useRouter } from 'next/router';
import { useEffect, useState, Fragment } from 'react';
import Link from 'next/link';
import NavBar from '../components/NavBar';
import Fab from '@material-ui/core/Fab';
import { Paper } from '@material-ui/core';

const Movies = () => {
	const [currentMovie, setCurrentMovie] = useState('');
	const router = useRouter();
	const { movieId } = router.query;
	const fetchData = async () => {
		const res = await axios(
			`http://www.omdbapi.com/?i=${movieId}&type=movie&apikey=${process.env.OmdbKey}`
		);
		setCurrentMovie(res.data);
	};
	useEffect(() => {
		fetchData();
	}, []);
	let rating = currentMovie.Ratings;
	if (rating) {
		rating = rating.map(each => {
			return (
				<span key={Math.random()} style={{ clear: 'both' }}>
					<b>{each.Source} : </b> {each.Value}
					<br />
				</span>
			);
		});
	}
	return (
		<Fragment>
			<NavBar />
			<br />
			<Fab style={{ float: 'right' }} variant='extended'>
				<Link href='/[movieId]/dict' as={`/${movieId}/dict`}>
					<a style={{ textDecoration: 'none', letterSpacing: '1.5px' }}>Words</a>
				</Link>
			</Fab>
			<span
				style={{
					display: 'flow-root',
					textAlign: 'center',
					fontSize: '27px',
					marginBottom: '0px',
					fontWeight: 'bold',
				}}
			>
				{currentMovie.Title}
			</span>
			{currentMovie ? (
				<div
					style={{
						position: 'relative',
						clear: 'right',
						clear: 'left',
						margin: '70px 45px',
					}}
				>
					<img
						src={currentMovie.Poster}
						alt='Movie poster'
						style={{ float: 'left', height: '283px', width: '195px' }}
					></img>
					<div style={{ margin: '0px auto', display: 'grid', paddingLeft: '30px' }}>
						<span
							style={{
								fontSize: '14px',
								WebkitBoxDecorationBreak: 'clone',
								padding: '0px 0px 0px 20px',
							}}
						>
							{currentMovie.Plot}
						</span>
						<br />
						<Paper elevation={3} style={{ marginTop: '10px', display: 'flex' }}>
							<span
								style={{
									padding: '15px 15px',
								}}
							>
								<span style={{ fontStyle: 'bold' }}>
									<b>Released on :</b> {currentMovie.Released}
								</span>
								<br />
								<span style={{ fontStyle: 'bold' }}>
									<b>Directed by: </b>
									{currentMovie.Director}
								</span>
								<br />
								<span style={{ fontStyle: 'bold' }}>
									<b>Genre:</b> {currentMovie.Genre}
								</span>
								<br />
								<span style={{ fontStyle: 'bold' }}>
									<b>Cast :</b> {currentMovie.Actors}
								</span>
							</span>
						</Paper>
						<br />
						<br />
						<Paper elevation={3} style={{ display: 'flex' }}>
							<span style={{ padding: '15px 15px' }}>{rating}</span>
						</Paper>
					</div>
				</div>
			) : (
				<p>Loading...</p>
			)}
		</Fragment>
	);
};

export default Movies;
