import React, { Fragment, useState } from 'react';
import { useRouter } from 'next/router';
import Axios from 'axios';
import fetch from 'isomorphic-unfetch';

const dictionary = ({ data }) => {
	const [searchWord, setSearchWord] = useState('');
	//   const [searchResult, setSearchResult] = useState("");
	const router = useRouter();
	const handleChange = async e => {
		setSearchWord(e.target.value);
	};
	const handleSubmit = async e => {
		e.preventDefault();
		const searchForm = {
			id: router.query.movieId,
			// title: ,
			word: searchWord,
		};
		const config = {
			headers: {
				'Content-Type': 'application/json',
			},
		};
		const body = JSON.stringify(searchForm);
		console.log(body);
		const res = await Axios.post(`/movies/${router.query.movieId}/dict`, body, config);
		console.log(res);
		console.log('Reached the end!!!');
	};
	const { movieId } = router.query;
	return (
		<div>
			{console.log(data)}
			<p>Movie Title: {data.Title}</p>
			{movieId}
			<form onSubmit={e => handleSubmit(e)}>
				<label>Search</label>
				<input type='text' value={searchWord} onChange={e => handleChange(e)}></input>
			</form>
			<p>Hello dictionary!!!</p>
		</div>
	);
};

dictionary.getInitialProps = async ctx => {
	if (ctx.query) {
		console.log(ctx.query.movieId);
		const res = await fetch(
			`http://www.omdbapi.com/?i=${ctx.query.movieId}type=movie&apikey=${process.env.OmdbKey}`
		);
		const data = await res.json();
		return { data };
	}
};

export default dictionary;
