import axios from 'axios';
import { useRouter } from 'next/router';
import { useEffect, useState, Fragment } from 'react';
import Link from 'next/link';
import NavBar from '../components/NavBar';
import Fab from '@material-ui/core/Fab';

const Movies = () => {
	const [currentMovie, setCurrentMovie] = useState('');
	const router = useRouter();
	const { movieId } = router.query;
	const fetchData = async () => {
		const res = await axios(
			`http://www.omdbapi.com/?i=${movieId}&type=movie&apikey=${process.env.OmdbKey}`
		);
		setCurrentMovie(res.data);
	};
	useEffect(() => {
		fetchData();
	}, []);
	let rating = currentMovie.Ratings;
	if (rating) {
		rating = rating.map(each => {
			return (
				<div key={Math.random()}>
					<p>
						{each.Source}: {each.Value}
					</p>
				</div>
			);
		});
	}
	return (
		<Fragment>
			<NavBar />
			<br />
			<p style={{ margin: '0 auto' }}>Movie Description</p>
			<Fab style={{ float: 'right' }} variant='extended'>
				<Link href='/[movieId]/dict' as={`/${movieId}/dict`}>
					<a style={{ textDecoration: 'none' }}>Dictionary</a>
				</Link>
			</Fab>
			<br />
			{currentMovie ? (
				<div style={{ position: 'relative' }}>
					<img
						src={currentMovie.Poster}
						alt='Movie poster'
						style={{ float: 'left' }}
					></img>
					<div style={{ clear: 'right' }}>
						<p>Movie Name: {currentMovie.Title}</p>
						<p>Released on : {currentMovie.Released}</p>
						<p>Directed by: {currentMovie.Director}</p>
						<p>Genre: {currentMovie.Genre}</p>
						<span>{rating}</span>
					</div>
				</div>
			) : (
				<p>Loading...</p>
			)}
		</Fragment>
	);
};

export default Movies;
