import React, { Fragment, useState, useEffect } from 'react';
import Head from 'next/head';
import Axios from 'axios';
import fetch from 'isomorphic-unfetch';
import { useRouter } from 'next/router';
import WordResults from '../../components/WordResults';
import NavBar from '../../components/NavBar';
import { Container, Button, TextField } from '@material-ui/core';

const dictionary = ({ data }) => {
	const [searchWord, setSearchWord] = useState('');
	const [words, setWords] = useState([]);
	const [matchedWords, setMatchedWords] = useState([]);
	const [wordErrors, setWordErrors] = useState();

	const handleChange = async e => {
		setSearchWord(e.target.value);
	};
	const router = useRouter();
	const {
		imdbID,
		Title,
		Year,
		Poster,
		Genre,
		Director,
		Actors,
		Metascore,
		imdbRating,
		imdbVotes,
	} = data;

	const handleSubmit = async e => {
		e.preventDefault();
		const searchForm = {
			imdbID,
			Title,
			searchWord,
			Year,
			Poster,
			Genre,
			Director,
			Actors,
			Metascore,
			imdbRating,
			imdbVotes,
		};
		try {
			const config = {
				headers: {
					'Content-Type': 'application/json',
				},
			};
			const body = JSON.stringify(searchForm);
			const res = await Axios.post(`/api/${router.query.movieId}/dict`, body, config);
			// console.log(res.data.errors[0].msg);
			setWordErrors(res.data.errors[0].msg);
			setSearchWord('');
			const config2 = {
				'Content-Type': 'application/json',
			};
			Axios.get(`/api/${router.query.movieId}/dict`, config2).then(res => {
				setWords(res);
			});
			alert(res.data);
		} catch (err) {
			if (err) throw err;
			console.log(err);
		}
	};

	useEffect(() => {
		const config = {
			'Content-Type': 'application/json',
		};
		Axios.get(`/api/${router.query.movieId}/dict`, config).then(res => {
			setWords(res);
		});
	}, []);

	const handleSearch = async e => {
		if (!words.data.length) {
			alert(`No word is added yet!`);
		} else {
			const res = await Axios.get(`/api/${data.imdbID}/dict/matched`);
			if (res.data === 'No subtitles found') {
				alert('No subtitles was found!!!');
			} else {
				words.data.map(each => {
					let result = res.data.data.search(each.word);
					if (result !== -1 && matchedWords.indexOf(each.word) === -1) {
						setMatchedWords(oldArray => [...oldArray, each.word]);
					}
				});
			}
		}
	};

	return (
		<div>
			{console.log(wordErrors)}
			<Head>
				<title>Movies Words</title>
			</Head>
			<NavBar />
			<p style={{ textAlign: 'center', fontSize: '20px' }}>{data.Title}</p>
			<Container>
				<form
					method='POST'
					action={`/api/${data.imdbID}/subtitles`}
					enctype='multipart/form-data'
				>
					<input
						style={{ fontSize: '17px', width: '225px' }}
						type='file'
						name='profile'
						placeholder="Upload it's subtitles"
					/>
					<button style={{ fontSize: '17px' }}>Upload</button>
				</form>
				<br />
				<Button variant='contained' onClick={e => handleSearch(e)}>
					Search
				</Button>
				<br />
				<br />
				<form onSubmit={e => handleSubmit(e)}>
					<TextField
						value={searchWord}
						error
						label={wordErrors ? 'Error' : null}
						helperText={wordErrors}
						onChange={e => handleChange(e)}
						placeholder='Enter the word'
					></TextField>
					<button type='submit' className='btn btn-primary'>
						Submit
					</button>
				</form>
				<br />
				{matchedWords.length > 0 && <div>{matchedWords}</div>}
				<br />
				<WordResults words={words} />
			</Container>
		</div>
	);
};

dictionary.getInitialProps = async ctx => {
	if (ctx.query.movieId) {
		const res = await fetch(
			`http://www.omdbapi.com/?i=${ctx.query.movieId}&type=movie&apikey=${process.env.OmdbKey}`
		);
		const data = await res.json();
		return { data };
	}
};

export default dictionary;
