import React, { useState, useEffect, Children } from 'react';
import Axios from 'axios';
import { useRouter } from 'next/router';

const WordResults = ({ data }) => {
	const [words, setWords] = useState([]);
	const router = useRouter();

	useEffect(() => {
		const config = {
			'Content-Type': 'application/json',
		};
		Axios.get(`/api/${router.query.movieId}/dict`, config).then(res => {
			console.log(res);
			setWords(res);
		});
	}, []);

	return (
		<div>
			<p>{data.Title} Dictionary...</p>
			{words.data ? (
				words.data.length ? (
					<div>
						{words.data.map(each => {
							return (
								<span key={each.id}>
									{each.word} {'  '}
								</span>
							);
						})}
					</div>
				) : (
					<p>Nothing found!!!</p>
				)
			) : (
				<p>Loading...</p>
			)}
		</div>
	);
};

export default WordResults;
