import React, { useEffect } from 'react';
import ExposurePlus1TwoToneIcon from '@material-ui/icons/ExposurePlus1TwoTone';
import ExposureNeg1TwoToneIcon from '@material-ui/icons/ExposureNeg1TwoTone';
import Axios from 'axios';

const WordResults = ({ words }) => {
	useEffect(() => {
		console.log(words);
	}, [words]);
	const handleClick = async (word_id, vote) => {
		const value = localStorage.getItem('user');
		if (value) {
			localStorage.clear();
			const word_id_var = {
				word_id: `${word_id}`,
				vote: `${vote}`,
			};
			const body = JSON.stringify(word_id_var);
			console.log(body);
			const config = {
				headers: {
					'Content-Type': 'application/json',
				},
			};
			const res = await Axios.post(`/api/:id/dict/${word_id}`, body, config);
			alert(res.data);
		} else {
			alert('You can vote just once per session!!!');
		}
	};
	let wordList;
	if (words.data) {
		wordList = words.data.map(each => {
			return (
				<span key={each.id}>
					{each.word} {'   '}
					<ExposurePlus1TwoToneIcon onClick={() => handleClick(each.id, +1)} />{' '}
					{each.upvotes}{' '}
					<ExposureNeg1TwoToneIcon onClick={() => handleClick(each.id, -1)} />
					{'   '}
				</span>
			);
		});
	}

	return (
		<div>
			{words.data ? (
				words.data.length ? (
					<div>{wordList}</div>
				) : (
					<p>Nothing found!!!</p>
				)
			) : (
				<div>
					{console.log(`Was loading!!`)}
					<p>Loading...</p>
				</div>
			)}

			{words.length + 1
				? console.log(`Still loading!!!`)
				: words.data.length
				? console.log(`Found!!!`)
				: console.log(`Nothing Found!!!`)}
		</div>
	);
};

export default WordResults;
