import React, { useState, useEffect } from 'react';
import Axios from 'axios';
import { useRouter } from 'next/router';

const WordResults = ({ data }) => {
	const [words, setWords] = useState([]);
	const router = useRouter();

	// const fetchWords = async () => {
	// const body = data.movieId;
	// const config = {
	// 	'Content-Type': 'application/json',
	// };
	// const res = await Axios.get(`/api/${router.query.movieId}/dict`, config);
	// setWords(res.data);
	// };

	useEffect(() => {
		const config = {
			'Content-Type': 'application/json',
		};
		Axios.get(`/api/${router.query.movieId}/dict`, config).then(res => {
			setWords(res.data);
		});
		fetchWords();
	}, [words]);

	return (
		<div>
			<p>{data.Title} Dictionary...</p> {console.log(words)}
		</div>
	);
};

export default WordResults;
