import React, { Fragment, useState, useEffect } from 'react';
import Head from 'next/head';
import Axios from 'axios';
import fetch from 'isomorphic-unfetch';
import { useRouter } from 'next/router';
import WordResults from '../../components/WordResults';
import { Button, Form, FormGroup, Label, Input } from 'reactstrap';

const dictionary = ({ data }) => {
	const [searchWord, setSearchWord] = useState('');
	const [words, setWords] = useState([]);
	const [matchedWords, setMatchedWords] = [];

	const handleChange = async e => {
		setSearchWord(e.target.value);
	};
	const router = useRouter();
	const {
		imdbID,
		Title,
		Year,
		Poster,
		Genre,
		Director,
		Actors,
		Metascore,
		imdbRating,
		imdbVotes,
	} = data;

	const handleSubmit = async e => {
		e.preventDefault();
		const searchForm = {
			imdbID,
			Title,
			searchWord,
			Year,
			Poster,
			Genre,
			Director,
			Actors,
			Metascore,
			imdbRating,
			imdbVotes,
		};
		try {
			const config = {
				headers: {
					'Content-Type': 'application/json',
				},
			};
			const body = JSON.stringify(searchForm);
			const res = await Axios.post(`/api/${router.query.movieId}/dict`, body, config);
			setSearchWord('');
			const config2 = {
				'Content-Type': 'application/json',
			};
			Axios.get(`/api/${router.query.movieId}/dict`, config2).then(res => {
				setWords(res);
			});
			alert(res.data);
		} catch (err) {
			if (err) throw err;
			console.log(err);
		}
	};

	useEffect(() => {
		const config = {
			'Content-Type': 'application/json',
		};
		Axios.get(`/api/${router.query.movieId}/dict`, config).then(res => {
			setWords(res);
		});
	}, []);

	const handleSearch = async e => {
		if (words.data.length) {
			alert(`No word is added yet!`);
		} else {
			const res = await Axios.get(`/api/${data.imdbID}/dict/matched`);
			console.log(`From search word ${res}`);
		}
		// let fileWords = res.data;
		// console.log(words);
	};

	return (
		<div className='container'>
			<Head>
				<title>Next App</title>
			</Head>
			<p>Movie Title: {data.Title}</p>
			<form
				method='POST'
				action={`/api/${data.imdbID}/subtitles`}
				enctype='multipart/form-data'
			>
				<input type='file' name='profile' placeholder="Upload it's subtitles" />
				<button>Upload</button>
			</form>
			<br />
			<button onClick={e => handleSearch(e)}>Search</button>
			<br />
			<form onSubmit={e => handleSubmit(e)}>
				{/* <FormGroup> */}
				<input
					value={searchWord}
					className='form-control'
					onChange={e => handleChange(e)}
					placeholder='Enter the word'
				></input>
				{/* </FormGroup> */}
				<button type='submit' className='btn btn-primary'>
					Submit
				</button>
			</form>
			<br />
			<WordResults words={words} />
		</div>
	);
};

dictionary.getInitialProps = async ctx => {
	if (ctx.query.movieId) {
		const res = await fetch(
			`http://www.omdbapi.com/?i=${ctx.query.movieId}&type=movie&apikey=${process.env.OmdbKey}`
		);
		const data = await res.json();
		return { data };
	}
};

export default dictionary;
