import React, { useState, useEffect, Children } from 'react';
import Axios from 'axios';
import { useRouter } from 'next/router';

const WordResults = ({ data }) => {
	const [words, setWords] = useState([]);
	const router = useRouter();
	let wordList = [];

	useEffect(() => {
		const config = {
			'Content-Type': 'application/json',
		};
		Axios.get(`/api/${router.query.movieId}/dict`, config).then(res => {
			setWords(res.data);
		});
	}, []);
	if (words.length) {
		// console.log(words);
		wordList = words.map(each => {
			return (
				<div key={each.id}>
					<span>{each.word}</span>
				</div>
			);
			// console.log(each.word);
		});
	}

	return (
		<div>
			<p>{data.Title} Dictionary...</p>
			{console.log(wordList)}
			{wordList}
			{/* {words.length ? { wordList } : <p>No words found!!!</p>} */}
		</div>
	);
};

export default WordResults;
