import React, { Fragment, useState, useEffect } from 'react';
import Head from 'next/head';
import Axios from 'axios';
import fetch from 'isomorphic-unfetch';
import { useRouter } from 'next/router';
import WordResults from '../../components/WordResults';
import { Button, Form, FormGroup, Label, Input } from 'reactstrap';
import ExposureNeg1TwoToneIcon from '@material-ui/icons/ExposureNeg1TwoTone';

const dictionary = ({ data }) => {
	const [searchWord, setSearchWord] = useState('');
	const [words, setWords] = useState({
		response: '',
		upIcon: '',
	});

	const handleChange = async e => {
		setSearchWord(e.target.value);
	};
	const router = useRouter();
	const {
		imdbID,
		Title,
		Year,
		Poster,
		Genre,
		Director,
		Actors,
		Metascore,
		imdbRating,
		imdbVotes,
	} = data;

	const handleSubmit = async e => {
		e.preventDefault();
		const searchForm = {
			imdbID,
			Title,
			searchWord,
			Year,
			Poster,
			Genre,
			Director,
			Actors,
			Metascore,
			imdbRating,
			imdbVotes,
		};
		try {
			const config = {
				headers: {
					'Content-Type': 'application/json',
				},
			};
			const body = JSON.stringify(searchForm);
			const res = await Axios.post(`/api/${router.query.movieId}/dict`, body, config);
			setSearchWord('');
			const config2 = {
				'Content-Type': 'application/json',
			};
			Axios.get(`/api/${router.query.movieId}/dict`, config2).then(res => {
				setWords(res);
			});
			alert(res.data);
		} catch (err) {
			if (err) throw err;
			console.log(err);
		}
	};

	useEffect(() => {
		const config = {
			'Content-Type': 'application/json',
		};
		Axios.get(`/api/${router.query.movieId}/dict`, config).then(res => {
			setWords({ response: `${res}`, upIcon: `${ExposureNeg1TwoToneIcon}` });
		});
	}, []);

	return (
		<div className='container'>
			{console.log(words.response)}
			<Head>
				<title>Next App</title>
				<link
					rel='stylesheet'
					href='https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css'
					integrity='sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm'
					crossorigin='anonymous'
				/>
			</Head>
			<p>Movie Title: {data.Title}</p>
			<Form onSubmit={e => handleSubmit(e)}>
				<FormGroup>
					<Label>Search</Label>
					<Input
						type='text'
						value={searchWord}
						className='form-control'
						onChange={e => handleChange(e)}
						placeholder='Enter the word'
					></Input>
				</FormGroup>
				<Button type='submit' className='btn btn-primary'>
					Submit
				</Button>
			</Form>
			<br />
			<WordResults words={words} />
		</div>
	);
};

dictionary.getInitialProps = async ctx => {
	if (ctx.query.movieId) {
		const res = await fetch(
			`http://www.omdbapi.com/?i=${ctx.query.movieId}&type=movie&apikey=${process.env.OmdbKey}`
		);
		const data = await res.json();
		return { data };
	}
};

export default dictionary;
