import React, { useState, useEffect } from 'react';
import Axios from 'axios';
import { useRouter } from 'next/router';

const WordResults = ({ data }) => {
	const [words, setWords] = useState([]);
	const router = useRouter();

	const fetchWords = async () => {
		// const body = data.movieId;
		const config = {
			'Content-Type': 'application/json',
		};
		const res = await Axios.get(`/api/${router.query.movieId}/dict`, config);
		console.log(res);
		// const words = res[0].map(push(word));
		// console.log(words);
	};

	useEffect(() => {
		fetchWords();
	}, []);

	return <div>{data.Title} Dictionary...</div>;
};

export default WordResults;
