import React, { useState, useEffect } from 'react';
import Axios from 'axios';
import { useRouter } from 'next/router';

const WordResults = ({ data }) => {
	const [words, setWords] = useState([]);
	const router = useRouter();
	let wordList = [];
	// const fetchWords = async () => {
	// const body = data.movieId;
	// const config = {
	// 	'Content-Type': 'application/json',
	// };
	// const res = await Axios.get(`/api/${router.query.movieId}/dict`, config);
	// setWords(res.data);
	// };

	useEffect(() => {
		const config = {
			'Content-Type': 'application/json',
		};
		Axios.get(`/api/${router.query.movieId}/dict`, config).then(res => {
			setWords(res.data);
		});
	}, []);
	if (words.length) {
		wordList = words.map(each => {
			return (
				<div key={each.id}>
					<span>{each.word}</span>
				</div>
			);
		});
	}

	return (
		<div>
			<p>{data.Title} Dictionary...</p>
			{console.log(wordList)}
			{/* <span>{words.length ? { wordList } : <p>No words found!!!</p>}</span> */}
		</div>
	);
};

export default WordResults;
